customization.custom_css:
/* Custom typography */
h1 { color: #1E3A5F; font-family: 'Georgia', serif; letter-spacing: 0.05em; }
h2 { color: #2D5F8B; border-bottom: 2px solid #E8D5B7; padding-bottom: 8px; }

/* Custom section styling */
.section-card { background: linear-gradient(135deg, #F8F4EF 0%, #FFF 100%); border-radius: 12px; box-shadow: 0 2px 8px rgba(0,0,0,0.08); }

/* Rating stars color */
.rating-star { color: #D4A853; }

/* Life area cards */
.life-area-card { border-left: 4px solid #2D5F8B; }
